insert  into `db_news`(`id`,`class`,`title`,`flag`,`cont`,`time`) values (1,'公司新闻','江友企业开工大吉','y','<p><span style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: 12px; line-height: 34px; font-family: Verdana; background-color: rgb(240, 240, 240);\">江友企业是一家港商独资企业，创立于1995年6月。产品主要有各国认证插头电源线，电线电缆，接插件等，现已取得二十几个国家的安全认证，其中包括德国、韩国、英国、阿根廷、澳大利亚、美国、日本、以色列、巴西、南非、中国等，是同行中获得认证最多的企业之一。</span><span style=\"margin: 0px; padding: 0px; color: rgb(102, 102, 102); font-size: 12px; line-height: 34px; font-family: Verdana; background-color: rgb(240, 240, 240);\"><br/>&nbsp;&nbsp; &nbsp;&nbsp; 公司已在中国大陆拥有三个生产基地：其一，宁波江友电子有限公司在浙江省宁波市鄞州区洞桥镇明州工业园区购地29000m2，厂房面积29000m2；其二，杰友（赣州）电器配件有限公司在江西省赣州市全南县第二工业园区，购地56000m2，厂房面积37000m2；其三，横沥江友电子製品厂在广东省东莞市横沥镇，厂房面积10000m2。<br/>&nbsp;&nbsp; &nbsp;&nbsp; 为了创立优质品牌，公司已在中国、香港、日本、欧盟、美洲等国家与地区成功地注册了商标&quot;FILE&quot;、“江友”<span style=\"margin: 0px; padding: 0px; font-family: Tahoma;\"><span style=\"margin: 0px; padding: 0px; font-family: Verdana;\">、</span></span>“Light-Heavy”、“莱特海威”。公司秉着“诚、信、勤、准”之经营理念，经过不懈努力，定能成为同行中的佼佼者。</span><img src=\"/upload/image/201610/201610191476864020442068.jpg\" alt=\"201610191476864020442068.jpg\"/></p>',1476605377),(2,'公司新闻','知识培训123322','y','<p style=\"text-align: center;\"><img src=\"/upload/image/201610/201610161476609360672402.jpg\" title=\"201610161476609360672402.jpg\" alt=\"201411200825272283.jpg\"/><img src=\"/upload/image/201610/201610161476609366275901.jpg\" title=\"201610161476609366275901.jpg\" alt=\"201411200825278296.jpg\"/><img src=\"/upload/image/201610/201610161476609371501741.jpg\" title=\"201610161476609371501741.jpg\" alt=\"201411200825484421.jpg\"/><img src=\"/upload/image/201610/201610161476609376893313.jpg\" title=\"201610161476609376893313.jpg\" alt=\"201411200825499959.jpg\"/></p><p><br/></p><p><img src=\"/editor/dialogs/emotion/images/qpx/qpx_17.gif\"/></p>',1476609278),(7,'公司新闻','公司各项培训活动','y','<p><span style=\"color: rgb(102, 102, 102); font-family: 宋体, Arial, Verdana; font-size: 14px; line-h