/* Function  structure for function  `fnStripTags` */

/*!50003 DROP FUNCTION IF EXISTS `fnStripTags` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `fnStripTags`( Dirty text,len int ,sss varchar(10) ) RETURNS text CHARSET utf8
    DETERMINISTIC
BEGIN  
  DECLARE iStart, iEnd, iLength int;   
    WHILE Locate( '<', Dirty ) > 0 And Locate( '>', Dirty, Locate( '<', Dirty )) > 0 DO   
      BEGIN  
        SET iStart = Locate( '<', Dirty ), iEnd = Locate( '>', Dirty, Locate('<', Dirty ));   
        SET iLength = ( iEnd - iStart) + 1;   
        IF iLength > 0 THEN  
          BEGIN  
            SET Dirty = Insert( Dirty, iStart, iLength, ''); 
            set Dirty=replace(Dirty,'&nbsp;','');
          END;   
        END IF;   
      END;   
    END WHILE; 
     if char_length(Dirty)>len then
       return concat(left(Dirty,len-200),sss);
     else 
      return Dirty; 
     end if;     
END */$$
DELIMITER ;

